import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{r as i,o as t,c as r,a as o,b as l,w as n,d as s,e as c}from"./app-4086ba32.js";const d={},h=s('<h2 id="介绍" tabindex="-1"><a class="header-anchor" href="#介绍" aria-hidden="true">#</a> 介绍</h2><p>卡兹戴尔疯人院</p><blockquote><p>Katzdel Fool Community (简称KFC)</p></blockquote><div class="hint-container info"><p class="hint-container-title">社区制度</p><p>直接民主制</p></div><p>本社区为Lair服务器内测一周目的Rhodes Island——SP Branch社群衍生而来，原社区为生电机械高度发达的工业区，而KFC成员皆为原班人马。<br> 目前KFC社区位于热带草原风蚀山地山脚的一处村庄。</p><p>本社区主张和平对外原则，我们欢迎任何愿意以友好平等为前提的社区与我们进行建交，以助力彼此社区共同繁荣。<br> 但由于本社区成员内含常驻管理，故有保障玩家游戏体验的责任。本社区官方及其社区成员并不会对新玩家提供任何的装备、矿物物资供给，只提供最基本的食物补给和玩法引导。也希望您能日益强大，形成强大的社区团体或个人，我们期待与您的建交！</p><p>而因本服务器采取高度自治内含RP（Role Play角色扮演）元素的游戏玩法，故服务器并为设置领地类保护插件，玩家自觉性难以保证，故为保护社区安全，我们并不愿意公开社区的坐标，请您见谅。但这并不意味着我们闭关锁国与外界所隔绝，我们仍力争对每个新人友好的态度。</p><h2 id="成员" tabindex="-1"><a class="header-anchor" href="#成员" aria-hidden="true">#</a> 成员</h2><ul><li>Forest_Maple <strong>常驻管理</strong></li><li>ZERO_XKRT</li><li>meiqiguan</li><li>Kobayashi</li><li>ssxyh</li><li>NIDIE</li><li>yuzi233</li></ul><h2 id="官方机构" tabindex="-1"><a class="header-anchor" href="#官方机构" aria-hidden="true">#</a> 官方机构</h2><p>由于KFC成员大多皆为学生，很遗憾，自6月18日起Lair开服以来，KFC成员仍需忙于学业，故没有足够的精力进行上层建筑的建设与详细地方性法规的制定。<br> 目前将社区发展侧重点于经济发展，迫于地势KFC社区的选址不宜在开荒阶段大兴土木，故地方性标志建筑以及地方政府建筑将在我社科技水平达到一定程度时予以完善，届时KFC社区将以崭新姿态呈现在各位玩家面前。</p><h2 id="法规" tabindex="-1"><a class="header-anchor" href="#法规" aria-hidden="true">#</a> 法规</h2>',12);function p(u,_){const e=i("RouterLink");return t(),r("div",null,[h,o("p",null,[l(e,{to:"/law/kfc.html"},{default:n(()=>[c("卡兹戴尔疯人院法规")]),_:1})])])}const b=a(d,[["render",p],["__file","kfc.html.vue"]]);export{b as default};
